import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function AllTreks() {
  const [trekdetails, setTrekdetails] = useState({
    trekName: "",
    maxAltitude: "",
    trekKilometer: "",
    region: "",
    location: "",
    baseCamp: "",
    suitableFor: "",
    airport: "",
    railwayStation: "",
    season: "",
    grade: "",
    image: "" // Image URL or base64 encoded image string
  });

  useEffect(() => {
    axios.get("http://localhost:7070/home/getpartialtrek")
      .then(response => {
        console.log(response.data)
        setTrekdetails(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div className="container my-4">
    {trekdetails.trekName && (
      <div className="row" style={{ border: "2px solid #b4b4b4", padding: "10px", borderRadius: "10px", width: "50%" }}>
        <div className="col-md-4" style={{ border: "2px solid #6a5acd", padding: "10px", borderRadius: "6px" }}>
          <img src={trekdetails.image} alt="Trek Image" className="img-fluid" />
        </div>
        <div className="col-md-6">
          <h2>{trekdetails.trekName}</h2>
          <p>Location: {trekdetails.location}</p>
          <p>Duration: {trekdetails.duration}</p>
          <p>Approx: {trekdetails.trekKilometer}</p>
          <p>Grade: {trekdetails.grade}</p>
          <p>Altitude: {trekdetails.maxAltitude}</p>
          <a href="book-trek.html" className="btn btn-primary">View Details</a>
        </div>
      </div>
    )}
    </div>
  );
}

export default AllTreks;
