import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function AllTreks() {
    const [trekdetails, setTrekdetails] = useState([]);

    useEffect(() => {
      axios.get("http://localhost:7070/home/getpartialtrek")
        .then(response => {
          // Convert the relative image paths to actual URLs
          const treksWithImageUrls = response.data.map(trek => ({
            ...trek,
            image: `http://localhost:7070/${trek.image}`, // Assuming the server hosts images at this URL
          }));
          setTrekdetails(treksWithImageUrls);
        })
        .catch(error => {
          console.error(error);
        });
    }, []);

    return (
      <div className="container my-4">
        {trekdetails.map(trek => (
          <div key={trek.trekName} className="row" style={{ border: "2px solid #b4b4b4", padding: "10px", borderRadius: "10px", width: "50%" }}>
            <div className="col-md-4" style={{ border: "2px solid #6a5acd", padding: "10px", borderRadius: "6px" }}>
              <img src={trek.image} alt="Trek Image" className="img-fluid" />
            </div>
            <div className="col-md-6">
              <h2>{trek.trekName}</h2>
              <p>Location: {trek.location}</p>
              {/* Display other trek details here */}
              <a href="book-trek.html" className="btn btn-primary">View Details</a>
            </div>
          </div>
        ))}
      </div>
    );
  }

export default AllTreks;
