import React, { useEffect, useState } from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';

function AllTreks() {
  const [trekdetails, setTrekdetails] = useState([]);
  const [trekimages, setTrekimages] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:7070/home/getpartialdata")
      .then(response => {
        console.log(response.data);
        setTrekdetails(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    axios.get("http://localhost:7070/home/gettrekimages")
      .then(response => {
        console.log(response.data);
        setTrekimages(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div className="container my-4">
      {trekdetails.map((trek, index) => (
        <div key={trek.trekName} className="row" style={{ border: "2px solid #b4b4b4", padding: "10px", borderRadius: "10px", width: "50%" }}>

          <div className="col-md-4" style={{ border: "2px solid #6a5acd", padding: "10px", borderRadius: "6px" }}>
            {/* Render images separately using another map loop */}
            {trekimages.map((imageData, imageIndex) => (
              imageIndex === index && (
                <img key={imageIndex} src={`data:image/jpg;base64,${imageData}`} alt={`Trek Image ${imageIndex}`} className="img-fluid" />
              )
            ))}
          </div>

          <div className="col-md-6">
            <h2>{trek.trekName}</h2>
            <p>Location: {trek.location}</p>
            <p>Max Altitude: {trek.maxAltitude}</p>
            <p>Trek Kilometer: {trek.trekKilometer}</p>
            <p>Region: {trek.region}</p>
            <p>Base Camp: {trek.baseCamp}</p>
            <p>Suitable For: {trek.suitableFor}</p>
            <p>Airport: {trek.airport}</p>
            <p>Railway Station: {trek.railwayStation}</p>
            <p>Season: {trek.season}</p>
            <p>Grade: {trek.grade}</p>
            <a href="book-trek.html" className="btn btn-primary">View Details</a>
          </div>
        </div>
      ))}
    </div>
  );
}

export default AllTreks;
